import { Component, OnInit, OnDestroy, ViewChild, AfterViewInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule, ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatTableModule, MatTableDataSource } from '@angular/material/table';
import { MatPaginatorModule, MatPaginator } from '@angular/material/paginator';
import { MatSortModule, MatSort } from '@angular/material/sort';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatNativeDateModule } from '@angular/material/core';
import { MatDialogModule, MatDialog } from '@angular/material/dialog';
import { MatSnackBarModule, MatSnackBar } from '@angular/material/snack-bar';
import { MatChipsModule } from '@angular/material/chips';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

import { TransaccionService, FiltroTransacciones } from '../../core/services/transaccion.service';
import { FondoService } from '../../core/services/fondo.service';
import { NotificationService } from '../../core/services/notification.service';
import { Transaccion, TipoTransaccion, CategoriaTransaccion } from '../../core/models/transaccion.model';
import { Fondo } from '../../core/models/fondo.model';

@Component({
  selector: 'app-transacciones',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    MatTableModule,
    MatPaginatorModule,
    MatSortModule,
    MatCardModule,
    MatButtonModule,
    MatIconModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatDatepickerModule,
    MatNativeDateModule,
    MatDialogModule,
    MatSnackBarModule,
    MatChipsModule
  ],
  template: `
    <div class="transacciones-container">
      <!-- Filtros y acciones -->
      <mat-card class="filtros-card mb-2">
        <mat-card-header>
          <mat-card-title>Transacciones</mat-card-title>
          <div class="spacer"></div>
          <button mat-raised-button color="primary" (click)="abrirDialogoTransaccion()">
            <mat-icon>add</mat-icon>
            Nueva Transacción
          </button>
        </mat-card-header>
        <mat-card-content>
          <form [formGroup]="filtrosForm" class="filtros-form">
            <mat-form-field appearance="outline">
              <mat-label>Fondo</mat-label>
              <mat-select formControlName="fondoId">
                <mat-option value="">Todos los fondos</mat-option>
                <mat-option *ngFor="let fondo of fondos" [value]="fondo._id">
                  {{ fondo.nombre }}
                </mat-option>
              </mat-select>
            </mat-form-field>

            <mat-form-field appearance="outline">
              <mat-label>Tipo</mat-label>
              <mat-select formControlName="tipo">
                <mat-option value="">Todos</mat-option>
                <mat-option value="ingreso">Ingreso</mat-option>
                <mat-option value="gasto">Gasto</mat-option>
              </mat-select>
            </mat-form-field>

            <mat-form-field appearance="outline">
              <mat-label>Categoría</mat-label>
              <mat-select formControlName="categoria">
                <mat-option value="">Todas</mat-option>
                <mat-option *ngFor="let categoria of categorias" [value]="categoria">
                  {{ categoria | titlecase }}
                </mat-option>
              </mat-select>
            </mat-form-field>

            <button mat-raised-button color="accent" (click)="aplicarFiltros()">
              <mat-icon>filter_list</mat-icon>
              Filtrar
            </button>
            <button mat-button (click)="limpiarFiltros()">
              <mat-icon>clear</mat-icon>
              Limpiar
            </button>
          </form>
        </mat-card-content>
      </mat-card>

      <!-- Tabla de transacciones -->
      <mat-card class="tabla-card">
        <mat-card-content>
          <div class="table-container">
            <table mat-table [dataSource]="dataSource" matSort class="full-width">

              <ng-container matColumnDef="fecha">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Fecha </th>
                <td mat-cell *matCellDef="let transaccion"> 
                  {{ transaccion.fecha | date:'dd/MM/yyyy' }} 
                </td>
              </ng-container>

              <ng-container matColumnDef="descripcion">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Descripción </th>
                <td mat-cell *matCellDef="let transaccion"> 
                  {{ transaccion.descripcion }} 
                </td>
              </ng-container>

              <ng-container matColumnDef="fondo">
                <th mat-header-cell *matHeaderCellDef> Fondo </th>
                <td mat-cell *matCellDef="let transaccion"> 
                  {{ obtenerNombreFondo(transaccion.fondoId) }} 
                </td>
              </ng-container>

              <ng-container matColumnDef="categoria">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Categoría </th>
                <td mat-cell *matCellDef="let transaccion"> 
                  <span class="categoria-chip" 
                        [ngClass]="transaccion.tipo === 'ingreso' ? 'ingreso-chip' : 'gasto-chip'">
                    {{ transaccion.categoria | titlecase }}
                  </span>
                </td>
              </ng-container>

              <ng-container matColumnDef="tipo">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Tipo </th>
                <td mat-cell *matCellDef="let transaccion"> 
                  <span class="tipo-chip" 
                        [ngClass]="transaccion.tipo === 'ingreso' ? 'ingreso-chip' : 'gasto-chip'">
                    {{ transaccion.tipo | titlecase }}
                  </span>
                </td>
              </ng-container>

              <ng-container matColumnDef="monto">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Monto </th>
                <td mat-cell *matCellDef="let transaccion" 
                    [ngClass]="transaccion.tipo === 'ingreso' ? 'ingreso-text' : 'gasto-text'"> 
                  {{ (transaccion.tipo === 'ingreso' ? '+' : '-') + (transaccion.monto | currency:'COP':'symbol':'1.0-0') }}
                </td>
              </ng-container>

              <ng-container matColumnDef="acciones">
                <th mat-header-cell *matHeaderCellDef> Acciones </th>
                <td mat-cell *matCellDef="let transaccion">
                  <button mat-icon-button color="primary" (click)="editarTransaccion(transaccion)">
                    <mat-icon>edit</mat-icon>
                  </button>
                  <button mat-icon-button color="warn" (click)="eliminarTransaccion(transaccion)">
                    <mat-icon>delete</mat-icon>
                  </button>
                </td>
              </ng-container>

              <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
              <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table>
          </div>

          <mat-paginator 
            [pageSizeOptions]="[10, 25, 50]" 
            [pageSize]="10"
            [showFirstLastButtons]="true"
            (page)="onPageChange($event)"
            aria-label="Seleccionar página de transacciones">
          </mat-paginator>
        </mat-card-content>
      </mat-card>
    </div>
  `,
  styles: [`
    .transacciones-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
    }

    .filtros-card mat-card-header {
      display: flex;
      align-items: center;
    }

    .filtros-form {
      display: flex;
      gap: 16px;
      align-items: center;
      flex-wrap: wrap;
      margin-top: 16px;
    }

    .filtros-form mat-form-field {
      min-width: 150px;
    }

    .table-container {
      overflow-x: auto;
    }

    .categoria-chip,
    .tipo-chip {
      padding: 4px 8px;
      border-radius: 12px;
      font-size: 0.75em;
      font-weight: 500;
    }

    .ingreso-chip {
      background-color: #4caf50;
      color: white;
    }

    .gasto-chip {
      background-color: #f44336;
      color: white;
    }

    .ingreso-text {
      color: #4caf50;
      font-weight: 500;
    }

    .gasto-text {
      color: #f44336;
      font-weight: 500;
    }

    @media (max-width: 768px) {
      .transacciones-container {
        padding: 16px;
      }

      .filtros-form {
        flex-direction: column;
        align-items: stretch;
      }

      .filtros-form mat-form-field {
        min-width: auto;
      }
    }
  `]
})
export class TransaccionesComponent implements OnInit, OnDestroy, AfterViewInit {
  @ViewChild(MatPaginator) paginator!: MatPaginator;
  @ViewChild(MatSort) sort!: MatSort;

  private destroy$ = new Subject<void>();

  displayedColumns: string[] = ['fecha', 'descripcion', 'fondo', 'categoria', 'tipo', 'monto', 'acciones'];
  dataSource = new MatTableDataSource<Transaccion>();
  filtrosForm: FormGroup;

  fondos: Fondo[] = [];
  categorias: CategoriaTransaccion[] = [];
  transacciones: Transaccion[] = [];
  totalTransacciones = 0;
  currentPage = 1;
  pageSize = 10;
  loading = false;

  constructor(
    private fb: FormBuilder,
    private transaccionService: TransaccionService,
    private fondoService: FondoService,
    private dialog: MatDialog,
    private snackBar: MatSnackBar,
    private notificationService: NotificationService
  ) {
    this.filtrosForm = this.fb.group({
      fondoId: [''],
      tipo: [''],
      categoria: ['']
    });
  }

  ngOnInit(): void {
    this.cargarDatosIniciales();
    this.cargarTransacciones();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  ngAfterViewInit(): void {
    this.dataSource.paginator = this.paginator;
    this.dataSource.sort = this.sort;
  }

  cargarDatosIniciales(): void {
    // Cargar fondos
    this.fondoService.obtenerFondos()
      .pipe(takeUntil(this.destroy$))
      .subscribe({
        next: (fondos) => this.fondos = fondos,
        error: (error) => {
          console.error('Error al cargar fondos:', error);
          this.notificationService.error('Error al cargar fondos');
        }
      });

    // Cargar categorías
    this.categorias = this.transaccionService.obtenerCategorias();
  }

  cargarTransacciones(): void {
    this.loading = true;
    const filtros: FiltroTransacciones = {
      ...this.filtrosForm.value,
      page: this.currentPage,
      limit: this.pageSize
    };

    // Limpiar valores vacíos
    Object.keys(filtros).forEach(key => {
      if (filtros[key as keyof FiltroTransacciones] === '') {
        delete filtros[key as keyof FiltroTransacciones];
      }
    });

    this.transaccionService.obtenerTransacciones(filtros)
      .pipe(takeUntil(this.destroy$))
      .subscribe({
        next: (response) => {
          this.transacciones = response.transacciones;
          this.totalTransacciones = response.total;
          this.dataSource.data = this.transacciones;
          this.loading = false;
        },
        error: (error) => {
          console.error('Error al cargar transacciones:', error);
          this.notificationService.error('Error al cargar transacciones');
          this.loading = false;
        }
      });
  }

  aplicarFiltros(): void {
    this.currentPage = 1;
    this.cargarTransacciones();
  }

  limpiarFiltros(): void {
    this.filtrosForm.reset();
    this.currentPage = 1;
    this.cargarTransacciones();
  }

  onPageChange(event: any): void {
    this.currentPage = event.pageIndex + 1;
    this.pageSize = event.pageSize;
    this.cargarTransacciones();
  }

  abrirDialogoTransaccion(transaccion?: Transaccion): void {
    // Por ahora mostraremos un mensaje, luego implementaremos el diálogo
    const accion = transaccion ? 'editar' : 'crear';
    this.notificationService.info(`Función para ${accion} transacción - Por implementar`);
  }

  editarTransaccion(transaccion: Transaccion): void {
    this.abrirDialogoTransaccion(transaccion);
  }

  eliminarTransaccion(transaccion: Transaccion): void {
    if (confirm('¿Estás seguro de que deseas eliminar esta transacción?')) {
      this.transaccionService.eliminarTransaccion(transaccion._id!)
        .pipe(takeUntil(this.destroy$))
        .subscribe({
          next: () => {
            this.notificationService.success('Transacción eliminada correctamente');
            this.cargarTransacciones();
          },
          error: (error) => {
            console.error('Error al eliminar transacción:', error);
            this.notificationService.error('Error al eliminar la transacción');
          }
        });
    }
  }

  obtenerNombreFondo(fondoId: string): string {
    const fondo = this.fondos.find(f => f._id === fondoId);
    return fondo ? fondo.nombre : 'Fondo no encontrado';
  }
}
