import { Routes } from '@angular/router';
import { AuthGuard } from './core/guards/auth.guard';
import { NoAuthGuard } from './core/guards/no-auth.guard';

export const routes: Routes = [
  {
    path: '',
    redirectTo: '/dashboard',
    pathMatch: 'full'
  },
  {
    path: 'login',
    loadComponent: () => import('./features/auth/login.component').then(m => m.LoginComponent),
    canActivate: [NoAuthGuard]
  },
  {
    path: 'dashboard',
    loadComponent: () => import('./features/dashboard/dashboard.component').then(m => m.DashboardComponent),
    canActivate: [AuthGuard]
  },
  {
    path: 'transacciones',
    loadComponent: () => import('./features/transacciones/transacciones.component').then(m => m.TransaccionesComponent),
    canActivate: [AuthGuard]
  },
  {
    path: 'fondos',
    loadComponent: () => import('./features/fondos/fondos.component').then(m => m.FondosComponent),
    canActivate: [AuthGuard]
  },
  {
    path: 'reportes',
    loadComponent: () => import('./features/reportes/reportes.component').then(m => m.ReportesComponent),
    canActivate: [AuthGuard]
  },
  {
    path: 'test-connection',
    loadComponent: () => import('./features/test-connection/test-connection.component').then(m => m.TestConnectionComponent)
    // Sin guard para permitir acceso sin autenticación
  },
  {
    path: '**',
    redirectTo: '/dashboard'
  }
];
