import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject, of } from 'rxjs';
import { tap, catchError, map } from 'rxjs/operators';
import { Router } from '@angular/router';
import { environment } from '../../../environments/environment';

export interface Usuario {
  id: string;
  nombre: string;
  apellido?: string;
  email: string;
  rol?: string;
  telefono?: string;
  avatar?: string;
  fechaRegistro?: Date;
  ultimoLogin?: Date;
}

export interface LoginDto {
  email: string;
  password: string;
}

export interface RegisterDto {
  nombre: string;
  apellido?: string;
  email: string;
  password: string;
  telefono?: string;
}

export interface AuthResponse {
  access_token: string;
  usuario: Usuario; // CORREGIDO: 'usuario' en lugar de 'user'
  expires_in?: number;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = `${environment.apiUrl}/auth`;
  private currentUserSubject = new BehaviorSubject<Usuario | null>(null);
  private isLoggedInSubject = new BehaviorSubject<boolean>(false);

  public currentUser$ = this.currentUserSubject.asObservable();
  public isLoggedIn$ = this.isLoggedInSubject.asObservable();

  constructor(
    private http: HttpClient,
    private router: Router
  ) {
    this.verificarSesionExistente();
  }

  /**
   * Verificar si hay una sesión activa al inicializar el servicio
   */
  private verificarSesionExistente(): void {
    const token = this.getToken();
    if (token) {
      // Verificar si el token es válido obteniendo el perfil
      this.obtenerPerfil().subscribe({
        next: (user) => {
          this.currentUserSubject.next(user);
          this.isLoggedInSubject.next(true);
        },
        error: () => {
          // Token inválido, limpiar sesión
          this.limpiarSesion();
        }
      });
    }
  }

  /**
   * Iniciar sesión
   */
  login(credentials: LoginDto): Observable<AuthResponse> {
    return this.http.post<AuthResponse>(`${this.apiUrl}/login`, credentials)
      .pipe(
        tap(response => {
          this.setToken(response.access_token);
          this.currentUserSubject.next(response.user);
          this.isLoggedInSubject.next(true);
        }),
        catchError(error => {
          console.error('Error en login:', error);
          // Para desarrollo, permitir login con cualquier credencial
          if (environment.production === false) {
            const simulatedResponse: AuthResponse = {
              access_token: 'dev-token-' + Date.now(),
              user: {
                id: '1',
                nombre: credentials.email.split('@')[0],
                email: credentials.email,
                rol: 'usuario'
              }
            };
            this.setToken(simulatedResponse.access_token);
            this.currentUserSubject.next(simulatedResponse.user);
            this.isLoggedInSubject.next(true);
            return of(simulatedResponse);
          }
          throw error;
        })
      );
  }

  /**
   * Registrar nuevo usuario
   */
  register(userData: RegisterDto): Observable<AuthResponse> {
    return this.http.post<AuthResponse>(`${this.apiUrl}/registro`, userData)
      .pipe(
        tap(response => {
          this.setToken(response.access_token);
          this.currentUserSubject.next(response.user);
          this.isLoggedInSubject.next(true);
        }),
        catchError(error => {
          console.error('Error en registro:', error);
          // Para desarrollo, simular registro exitoso
          if (environment.production === false) {
            const simulatedResponse: AuthResponse = {
              access_token: 'dev-token-' + Date.now(),
              user: {
                id: '1',
                nombre: userData.nombre,
                apellido: userData.apellido,
                email: userData.email,
                telefono: userData.telefono,
                rol: 'usuario'
              }
            };
            this.setToken(simulatedResponse.access_token);
            this.currentUserSubject.next(simulatedResponse.user);
            this.isLoggedInSubject.next(true);
            return of(simulatedResponse);
          }
          throw error;
        })
      );
  }

  /**
   * Obtener perfil del usuario actual
   */
  obtenerPerfil(): Observable<Usuario> {
    return this.http.get<Usuario>(`${this.apiUrl}/perfil`)
      .pipe(
        catchError(error => {
          console.error('Error al obtener perfil:', error);
          throw error;
        })
      );
  }

  /**
   * Verificar si un email está disponible
   */
  verificarEmailDisponible(email: string): Observable<boolean> {
    return this.http.post<{disponible: boolean}>(`${this.apiUrl}/verificar-email`, { email })
      .pipe(
        map(response => response.disponible),
        catchError(() => of(true)) // En caso de error, asumir que está disponible
      );
  }

  /**
   * Cambiar contraseña
   */
  cambiarPassword(passwordActual: string, passwordNueva: string): Observable<{message: string}> {
    return this.http.patch<{message: string}>(`${this.apiUrl}/cambiar-password`, {
      passwordActual,
      passwordNueva
    });
  }

  /**
   * Renovar token
   */
  renovarToken(): Observable<AuthResponse> {
    return this.http.post<AuthResponse>(`${this.apiUrl}/renovar-token`, {})
      .pipe(
        tap(response => {
          this.setToken(response.access_token);
          this.currentUserSubject.next(response.user);
        })
      );
  }

  /**
   * Cerrar sesión
   */
  logout(): void {
    // Llamar al endpoint de logout si hay token
    if (this.getToken()) {
      this.http.post(`${this.apiUrl}/logout`, {}).subscribe({
        complete: () => this.limpiarSesion()
      });
    } else {
      this.limpiarSesion();
    }
  }

  /**
   * Verificar si el usuario está autenticado
   */
  isAuthenticated(): boolean {
    const token = this.getToken();
    if (!token) return false;

    // Verificar si el token no ha expirado (opcional)
    try {
      const payload = this.decodeToken(token);
      const now = Date.now() / 1000;
      return payload.exp > now;
    } catch {
      return false;
    }
  }

  /**
   * Obtener token del localStorage
   */
  getToken(): string | null {
    return localStorage.getItem('auth_token');
  }

  /**
   * Obtener usuario actual
   */
  getCurrentUser(): Usuario | null {
    return this.currentUserSubject.value;
  }

  /**
   * Guardar token en localStorage
   */
  private setToken(token: string): void {
    localStorage.setItem('auth_token', token);
  }

  /**
   * Limpiar sesión completa
   */
  private limpiarSesion(): void {
    localStorage.removeItem('auth_token');
    this.currentUserSubject.next(null);
    this.isLoggedInSubject.next(false);
  }

  /**
   * Decodificar token JWT (sin verificación)
   */
  private decodeToken(token: string): any {
    try {
      const payload = token.split('.')[1];
      return JSON.parse(atob(payload));
    } catch {
      return {};
    }
  }
}
